#include <iostream>
#include <algorithm>
using namespace std;
void Printfrequency(string str)
{
  sort(str.begin(), str.end());
  char ch = static_cast<char>(str[0]);
  int count = 1;
  for (size_t i = 1; i < str.length(); i++)
  {
    if (static_cast<char>(str[i]) == ch)
      count++;
    else
    {
      cout << ch << ":"<< count << " ";
      count = 1;
      ch = static_cast<char>(str[i]);
    }
  }
  cout << ch << ":"<< count << " \n";
}

int main()
{
  string str = "";
  Printfrequency(str);
  return 0;
}